import classes from "./Hotels.module.css";
const Hotels = () => {
  const Dummy_Hotels = [
    {
      name: "Aparthotel Stare Miasto",
      city: "Madrid",
      price: 120,
      rate: 8.9,
      type: "Excellent",
      image_url: "./images/hotel_1.webp",
    },
    {
      name: "Comfort Suites Airport",
      city: "Austin",
      price: 140,
      rate: 9.3,
      type: "Exceptional",
      image_url: "./images/hotel_2.jpg",
    },
    {
      name: "Four Seasons Hotel",
      city: "Lisbon",
      price: 99,
      rate: 8.8,
      type: "Excellent",
      image_url: "./images/hotel_3.jpg",
    },
    {
      name: "Hilton Garden Inn",
      city: "Berlin",
      price: 105,
      rate: 8.9,
      type: "Excellent",
      image_url: "./images/hotel_4.jpg",
    },
  ];

  return (
    <div className={classes.hotels}>
      <div>
        <img src={Dummy_Hotels[0].image_url} alt={Dummy_Hotels[0].name} />
        <div className={classes.text}>
          <h1>{Dummy_Hotels[0].name}</h1>
          <h3>{Dummy_Hotels[0].city} </h3>
          <h3>Stating from $ {Dummy_Hotels[0].price} </h3>
          <h3>
            {Dummy_Hotels[0].rate} {Dummy_Hotels[0].type}{" "}
          </h3>
        </div>
      </div>
    </div>
  );
};

export default Hotels;
